import React, { useState, useEffect } from "react";
import styled from "styled-components";
import Card from "../componets/Card";

const HomeBox = styled.div`
  height: 100vh;
  width: 100%;
  color: black;
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  gap: 20px;
`;

function Home() {
  const [data, setData] = useState("");
  useEffect(() => {
    fetch("http://techcrunch.com/wp-json/wp/v2/posts?perpage=100&content=embed")
      .then((res) => res.json())
      .then((data) => console.log(data))
      .then((data) => setData(data));
  }, []);

  if (data) {
    console.log(data);
  }

  return (
    <HomeBox>
      {data &&
        data((datas) => {
          return (
            <Card
              title={datas.title}
              img={datas.img}
              desc={datas.description}
            ></Card>
          );
        })}
      {data &&
        data((datas) => {
          return <h1>{datas.id}</h1>;[
            {
                "id": 2265236,
                "date": "2022-02-01T12:33:49",
                "date_gmt": "2022-02-01T20:33:49",
